package by.epam.training;

public class Lesson2_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//задание 20
		double l; //длина окружности
		double r; //радиус
		double s; //площадь
		
		l = 30.5;
		
		r = l / (2 * Math.PI);
		s = Math.PI * r * r;
		
		System.out.println("площадь круга " + s);
	}

}
